import logo from './logo.svg';
import './App.css';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import ContentBody from './components/ContentBody';
import { useState } from 'react';
import TaskEdit from './components/TaskEdit';

function App() {
  const [editPopupOpen, setEditPopupOpen]  =  useState(false);
  const [category, setCategory] = useState("Home");
  const [currentTaskId, setCurrentTaskId] = useState(-1);
  return (
    <div className="App" style={{minWidth:"1080px"}}>
      <Header />
      <div style={{ display: "flex", flexDirection: "row", height: "100vh",width : "100%" }}>
        <Sidebar editPopupState = {editPopupOpen} setEditPopupOpenState = {setEditPopupOpen} setCategory={setCategory} category={category} taskId={currentTaskId} setTaskId={setCurrentTaskId}/>
        <ContentBody editPopupState = {editPopupOpen} setEditPopupOpenState = {setEditPopupOpen} category={category} setCategory={setCategory} taskId={currentTaskId} setTaskId={setCurrentTaskId}/>
      </div>
    </div>
  );
}

export default App;
