import { HeaderContainer,HeaderSubContainer, PrimaryButton, SearchBar, svgIconStyle } from "./Utility";
import styled from "styled-components";
function Header() {
// Container to hold SearchBar and buttons
const SearchContainer = styled.div`
  display: flex;
  align-items: center;
  position: relative;
  width: 496px;
`;

// Styled SearchBar
const SearchBar = styled.input`
  background: #1b1b1b;
  color: #DDDDDD;
  border-radius: 128px;
  padding: 16px 32px;
  border: 1px solid #303030;
  width: 100%;
  height: 16px;
  text-align: left;
  outline: none; 
  font-size: 16px; 
  box-sizing: border-box;

  &:focus {
    background: #303030;
    color: #FFFFFF;
  }
`;

// Clear and Search buttons
const Button = styled.button`
  background: #303030;
  color: #DDDDDD;
  border: none;
  border-radius: 50%;
  padding: 8px;
  margin-left: 8px;
  cursor: pointer;
  font-size: 16px;
  width: 32px;
  height: 32px;
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    background: #1b1b1b;
  }

  &:focus {
    outline: none;
  }
`;

const ClearButton = styled(Button)`
  background: #ff5c5c; // Red color for clear button
`;

const SearchButton = styled(Button)`
  background: #4CAF50; // Green color for search button
`;
  const [query, setQuery] = React.useState('');

  const handleClear = () => setQuery('');
  const handleSearch = () => {
    // Implement search functionality here
    console.log('Searching for:', query);
  };

    return (
    <div style={{position:"relative"}}>
    <HeaderContainer>
        <HeaderSubContainer>PLAN R</HeaderSubContainer>
        <HeaderSubContainer>
            <SearchContainer>
            <SearchBar
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Search here"/>
            <ClearButton onClick={handleClear}>X</ClearButton>
            <SearchButton onClick={handleSearch}></SearchButton>
    </SearchContainer>
        </HeaderSubContainer>
        <HeaderSubContainer>
            <PrimaryButton>Account</PrimaryButton>
            <PrimaryButton><svg style={{...svgIconStyle, height : "20px", width : "20px"}} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><path d="M10 21H14C14 22.1 13.1 23 12 23S10 22.1 10 21M21 19V20H3V19L5 17V11C5 7.9 7 5.2 10 4.3V4C10 2.9 10.9 2 12 2S14 2.9 14 4V4.3C17 5.2 19 7.9 19 11V17L21 19M17 11C17 8.2 14.8 6 12 6S7 8.2 7 11V18H17V11Z" /></svg></PrimaryButton>
        </HeaderSubContainer>
    </HeaderContainer>
    </div>
  );
}

export default Header;
