import ForgotPassword from "./AuthRecovery";
import { LoginContainer, Title, AuthContainer, Login, Help, Signup, PrimaryButton, SecondaryButton } from "./Utility";
import { useState } from "react";
import { Navigate, useNavigate } from "react-router-dom";


function AuthSignup() {
    const [otpSent, setOtpSent] = useState(false);
    const navigate = useNavigate();

    const [sform, setSForm] = useState({
        name  : "",
        email : "",
        password: "",
        pin :""
      });

    const handleChanges = (e) => {
    const { name, value } = e.target;
    setSForm((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Form Data:', sform);

    const response = await fetch('http://localhost:5000/api/auth/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(sform),
    });

    const data = await response.json();

    if (response.ok) {
        // Now send OTP after successful signup
        await fetch('http://localhost:5000/api/auth/send-otp', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ email: sform.email }),
        });

        // Navigate to OTP verification page or show OTP input field
        navigate("/verify-otp"); // Change to your verification route
    } else {
        console.error(data.message);
    }
};



  const styles = {
    form: {
      display: 'flex',
      flexDirection: 'column',
      gap: '32px', // Space between form groups
      padding: '20px',
      maxWidth: '500px'
    },
    formGroup: {
      display: 'flex',
      flexDirection: 'column',
      gap: '5px', // Space between label and input
    },
    label: {
      fontSize: '16px',
      fontWeight: 'bold',
      textAlign : "left"
    },
    input: {
      padding: '10px',
      border: '1px solid #ddd',
      borderRadius: '4px',
      fontSize: '16px',
    },
    button: {
      alignSelf: 'flex-start',
    },
    };



  return (
    <div style={{minWidth:"1080px"}}>
      <LoginContainer>
        <Title>
          <h1>PLAN-R</h1>
          <p>An Intelligent activity planner and tracker</p>
        </Title>
        <Login>
              <h2 style={{alignSelf:"center"}}>Signup</h2>
            {!otpSent && <form onSubmit={handleSubmit} style={styles.form}>
              <div style={styles.formGroup}>
                <label htmlFor="name" style={styles.label}>Name:</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={sform.name}
                  onChange={handleChanges}
                  style={styles.input}
                />
              </div>
              <div style={styles.formGroup}>
                <label htmlFor="email" style={styles.label}>Email:</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={sform.email}
                  onChange={handleChanges}
                  style={styles.input}
                />
              </div>
              <PrimaryButton onClick={handleSubmit} type="submit" >Send OTP</PrimaryButton>
            </form>}
            {otpSent && <form onSubmit={handleSubmit} style={styles.form}>
              <div style={styles.formGroup}>
                <label htmlFor="name" style={styles.label}>Name:</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={sform.name}
                  onChange={handleChanges}
                  readOnly
                  style={styles.input}
                />
              </div>
              <div style={styles.formGroup}>
                <label htmlFor="email" style={styles.label}>Email:</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={sform.email}
                  readOnly
                  onChange={handleChanges}
                  style={styles.input}
                />
              </div>
              <div style={styles.formGroup}>
                <label htmlFor="otp" style={styles.label}>6 digit one time PIN :</label>
                <input
                  type="text"
                  id="pin"
                  name="pin"
                  value={sform.pin}
                  onChange={handleChanges}
                  style={styles.input}
                />
              </div>
              <div style={styles.formGroup}>
                <label htmlFor="password" style={styles.label}>Password :</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={sform.password}
                  onChange={handleChanges}
                  style={styles.input}
                />
              </div>
              <PrimaryButton onClick={handleSubmit} type="submit" >{otpSent ? "Signup":"Send OTP"}</PrimaryButton>
            </form> }
            <SecondaryButton onClick={()=>navigate("/login")}>Already have account?</SecondaryButton>
            </Login>
      </LoginContainer>
    </div>
  );
}

export default AuthSignup;
