import ForgotPassword from "./ForgotPassword";
import { LoginContainer, Title, AuthContainer, Login, Help, Signup, PrimaryButton, SecondaryButton } from "./Utility";
import { useState } from "react";


function AuthLogin(setAuth) {
  const [lform, setLForm] = useState({
    email  : "",
    password  : ""
  });

  const handleChanges = (e) => {
    const { name, value } = e.target;
    setLForm((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data:', lform);
    setAuth(true);
  };

  const styles = {
    form: {
      display: 'flex',
      flexDirection: 'column',
      gap: '32px', // Space between form groups
      padding: '20px',
      maxWidth: '500px'
    },
    formGroup: {
      display: 'flex',
      flexDirection: 'column',
      gap: '5px', // Space between label and input
    },
    label: {
      fontSize: '16px',
      fontWeight: 'bold',
      textAlign : "left"
    },
    input: {
      padding: '10px',
      border: '1px solid #ddd',
      borderRadius: '4px',
      fontSize: '16px',
    },
    button: {
      alignSelf: 'flex-start',
    },
    };



  return (
    <div style={{minWidth:"1080px"}}>
      <LoginContainer>
        <Title>
          <h1>PLAN-R</h1>
          <p>An Intelligent activity planner and tracker</p>
        </Title>
        <AuthContainer>
        <Login>
              <h2>Login</h2>
            <form onSubmit={handleSubmit} style={styles.form}>
              <div style={styles.formGroup}>
                <label htmlFor="email" style={styles.label}>Email:</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={lform.email}
                  onChange={handleChanges}
                  style={styles.input}
                />
              </div>
              <div style={styles.formGroup}>
                <label htmlFor="password" style={styles.label}>Password:</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={lform.password}
                  onChange={handleChanges}
                  style={styles.input}
                />
              </div>
              <PrimaryButton onClick={handleSubmit} type="submit" style={styles.button}>Login</PrimaryButton>
              <SecondaryButton>Forgot Password</SecondaryButton>
            </form>
            </Login>
        </AuthContainer>
      </LoginContainer>
    </div>
  );
}

export default AuthLogin;
