import { PrimaryButton, SidebarContainer, TopCTA, MenuItems, MenuItem, svgIconStyle, EditPopup, TaskItem } from "./Utility";
import React, { useState } from 'react';

const TaskEdit = () => {
  const [formData, setFormData] = useState({
    title: '',
    time: '',
    type: '',
    importantLevel: 1,
  });

  // Handle change for input fields
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Handle change for important level (which is a number)
  const handleImportantLevelChange = (e) => {
    const { value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      importantLevel: Number(value),
    }));
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data:', formData);
    // Perform form submission logic here
  };

  return (
    <EditPopup>
        <h2>Edit task details</h2>
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="title">Title:</label>
        <input
          type="text"
          id="title"
          name="title"
          value={formData.title}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="time">Time:</label>
        <input
          type="text"
          id="time"
          name="time"
          value={formData.time}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="type">Type:</label>
        <input
          type="text"
          id="type"
          name="type"
          value={formData.type}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="importantLevel">Important Level (out of 5):</label>
        <input
          type="number"
          id="importantLevel"
          name="importantLevel"
          min="1"
          max="5"
          value={formData.importantLevel}
          onChange={handleImportantLevelChange}
        />
      </div>
      <PrimaryButton type="submit">Submit</PrimaryButton>
    </form>
    </EditPopup>
  );
};

export default TaskEdit;
