import React from 'react';
import './App.css';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import ContentBody from './components/ContentBody';

function App() {
  return (
    <div className="App" style={{ height: "100vh" }}> {/* Ensuring App takes full viewport height */}
      <Header />
      <div style={{ display: "flex", flexDirection: "row", height: "calc(100vh - [header height])", width: "100%" }}> {/* Adjust height as needed */}
        <Sidebar />
        <ContentBody />
      </div>
    </div>
  );
}

export default App;
