import React, { useState } from 'react';
import { PrimaryButton, EditPopup, SecondaryButton, svgIconStyle } from './Utility'; // Import relevant component

const Notification = ({close, setNotify, notify}) => {
    // State to manage the selected option
  const [selectedOption, setSelectedOption] = useState('option2'); // Pre-selected option

  // Common styles for radio input (hidden)
  const radioStyle = {
    display: 'none',
  };

  // Style for the custom radio button appearance
  const spanStyle = {
    display: 'inline-block',
    width: '20px',
    height: '20px',
    borderRadius: '50%',
    border: '2px solid #007bff',
    backgroundColor: '#fff',
    marginRight: '8px',
    position: 'relative',
    verticalAlign: 'middle',
  };

  // Style for the checked state of the custom radio button
  const checkedSpanStyle = {
    ...spanStyle,
    backgroundColor: '#007bff',
    borderColor: '#007bff',
  };

  // Common styles for label and container
  const labelStyle = {
    display: 'flex',
    alignItems: 'center',
    cursor: 'pointer',
    marginBottom: '8px',
  };

  const containerStyle = {
    display: 'flex',
    alignItems: 'center',
  };

    return(
        <EditPopup style={{height: "460px"}}>
            <PrimaryButton onClick={()=>close(-1)} style={{width:"90px", padding:"12px 20px 12px 16px", background:"white", color:"black"}}>Close</PrimaryButton>
            <div>
                <p style={{fontSize:"32px", fontWeight:"600"}}>Notifications</p>
                <p style={{fontSize:"20px", fontWeight:"400"}}>select notification type</p>
            </div>
            <div>
      <div style={containerStyle}>
        <label style={labelStyle}>
          <input
            type="radio"
            name="option"
            value="option1"
            checked={selectedOption === 'option1'}
            onChange={() => setSelectedOption('option1')}
            style={radioStyle}
          />
          <span style={selectedOption === 'option1' ? checkedSpanStyle : spanStyle}></span>
          Option 1
        </label>
      </div>
      <div style={containerStyle}>
        <label style={labelStyle}>
          <input
            type="radio"
            name="option"
            value="option2"
            checked={selectedOption === 'option2'}
            onChange={() => setSelectedOption('option2')}
            style={radioStyle}
          />
          <span style={selectedOption === 'option2' ? checkedSpanStyle : spanStyle}></span>
          Option 2
        </label>
      </div>
      <div style={containerStyle}>
        <label style={labelStyle}>
          <input
            type="radio"
            name="option"
            value="option3"
            checked={selectedOption === 'option3'}
            onChange={() => setSelectedOption('option3')}
            style={radioStyle}
          />
          <span style={selectedOption === 'option3' ? checkedSpanStyle : spanStyle}></span>
          Option 3
        </label>
      </div>
    </div>
        </EditPopup>
    );
}

export default Notification;
