import React, { useState } from 'react';
import { PrimaryButton, EditPopup } from './Utility'; // Import relevant components

const TaskEdit = () => {
  const [formData, setFormData] = useState({
    title: '',
    time: '',
    type: '',
    importantLevel: 1,
  });

  // Handle change for input fields
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Handle change for important level (which is a number)
  const handleImportantLevelChange = (e) => {
    const { value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      importantLevel: Number(value),
    }));
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data:', formData);
    // Perform form submission logic here
  };

  return (
    <EditPopup>
      <h2>Edit Task Details</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <div style={styles.formGroup}>
          <label htmlFor="title" style={styles.label}>Title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            style={styles.input}
          />
        </div>
        <div style={styles.formGroup}>
          <label htmlFor="time" style={styles.label}>Time:</label>
          <input
            type="text"
            id="time"
            name="time"
            value={formData.time}
            onChange={handleChange}
            style={styles.input}
          />
        </div>
        <div style={styles.formGroup}>
          <label htmlFor="type" style={styles.label}>Type:</label>
          <input
            type="text"
            id="type"
            name="type"
            value={formData.type}
            onChange={handleChange}
            style={styles.input}
          />
        </div>
        <div style={styles.formGroup}>
          <label htmlFor="importantLevel" style={styles.label}>Important Level (out of 5):</label>
          <input
            type="number"
            id="importantLevel"
            name="importantLevel"
            min="1"
            max="5"
            value={formData.importantLevel}
            onChange={handleImportantLevelChange}
            style={styles.input}
          />
        </div>
        <PrimaryButton type="submit" style={styles.button}>Submit</PrimaryButton>
      </form>
    </EditPopup>
  );
};

// Inline styles for form elements
const styles = {
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px', // Space between form groups
    padding: '20px',
    maxWidth: '500px'
  },
  formGroup: {
    display: 'flex',
    flexDirection: 'column',
    gap: '20px', // Space between label and input
  },
  label: {
    fontSize: '16px',
    fontWeight: 'bold',
    textAlign : "left"
  },
  input: {
    padding: '10px',
    border: '1px solid #ddd',
    borderRadius: '4px',
    fontSize: '16px',
  },
  button: {
    alignSelf: 'flex-start',
  },
};

export default TaskEdit;
