import styled from "styled-components";

export const svgIconStyle = {
    fill : "#DDDDDD",
    height : "32px",
    width  : "32px"
}

export const HeaderContainer = styled.div`
background : black;
color   :   #DDDDDD;
padding :   12px 64px;
display :   flex;
flex-direction  :   row;
justify-content :   space-between;
`;

export const HeaderSubContainer = styled.div`
color   :   #DDDDDD;
padding :   8px;
display :   flex;
flex-direction  :   row;
font-weight : 600;
align-items :   center;
gap     :   20px;
`;

export const SearchBar = styled.input`
  background: #1b1b1b;
  color: #DDDDDD;
  border-radius: 128px;
  padding: 16px 32px;
  border: 1px solid #303030;
  width: 496px;
  height: 16px;
  text-align: left;
  outline: none; 
  font-size: 16px; 

  &:focus {
    background: #303030;
    color: #FFFFFF;
  }
`;


export const PrimaryButton = styled.div`
background  :   #1b1b1b;
color       :   #DDDDDD;
border-radius : 128px;
padding     : 16px 32px;
border      :   1px solid #303030;

&:hover {
    background: #303030;
    color: #FFFFFF;
  }
`;

export const SidebarContainer = styled.div`
position    : relative;
background : black;
color   :   #DDDDDD;
padding :   24px 0px;
display :   flex;
flex-direction  :   column;
border-top     :   1px solid #303030;
height: 100vh;
width   : 250px;
overflow-y  :   auto;
`;

export const TopCTA = styled.div`
color   :   #DDDDDD;
padding :   12px 16px 32px 16px;
display :   flex;
flex-direction  :   column;
font-weight : 600;
align-items :   center;
gap     :   20px;
`;

export const MenuItems = styled.div`
color   :   #DDDDDD;
display :   flex;
flex-direction  :   column;
font-weight : 600;
gap     :   2px;
`;

export const MenuItem = styled.div`
color   :   #DDDDDD;
padding :  16px 32px;
display :   flex;
flex-direction  :   row;
font-weight : 600;
align-items :   center;
gap     :   20px;
text-align  : left;
&:hover {
    background: rgb(35 52 67); /* Change background color on hover */
    color: #FFFFFF; /* Change text color on hover */
  }
`;


export const BodyContainer = styled.div`
background : white;
color   :   black;
padding :   24px 32px;
display :   flex;
flex-direction  :   row;
border-top     :   1px solid #303030;
height: 100vh;
width   :100%;
overflow-y  :   auto;
gap : 24px;
flex: 1;
`;

export const TaskGroup = styled.div`
background  : grey;
color       : black;
padding     : 24px;
display     : flex;
flex-direction  :   column;
gap         : 24px;
width : 100%;
`;

export const TaskItem = styled.div`
background  : grey;
color       : black;
padding     : 24px;
`;
