import { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { PrimaryButton, svgIconStyle, TaskGroup, TaskItem } from "./Utility";
import styled from "styled-components";
import { useCookies } from "react-cookie";

function Task() {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState([]);
  const [cookies] = useCookies(['jwt']);
  const [location, setLocation] = useState(useLocation().pathname);
  const [loading, setLoading] = useState(false);
  const arr = ["", "live", "completed", "timeup"];

  const EditButton = styled(PrimaryButton)`
    height: 20px;
    background: rgb(235 235 235);
    &:hover {
      background: rgb(249 206 137);
    }
  `;


  const setAsCompleted = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/task/completed/${id}`, {
        method: "POST",
        headers: {
          'Authorization': `Bearer ${cookies.jwt}`
        }
      });
      if (response.ok) {
        const data = await response.json();
        window.location.reload();
      } else {
        console.error('Failed to mark as completed', response.statusText);
      }
    } catch (error) {
      console.error('Error marking as completed', error);
    }
  };

  const setAsLive = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/task/live/${id}`, {
        method: "POST",
        headers: {
          'Authorization': `Bearer ${cookies.jwt}`
        }
      });
      if (response.ok) {
        const data = await response.json();
        window.location.reload();
      } else {
        console.error('Failed to mark as live', response.statusText);
      }
    } catch (error) {
      console.error('Error marking as live', error);
    }
  };

  useEffect(() => {
    const fetchTasks = async () => {
      setLoading(true);
      try {
        const url = arr.includes(location.slice(1).toLowerCase())
  ? location.slice(1).toLowerCase()==="" ? `http://localhost:5000/task/today?` :`http://localhost:5000/task/${location.slice(1).toLowerCase()}`
  : `http://localhost:5000/task/category/${location.slice(1).toLowerCase()}`;
  const response = await fetch(url, {
          headers: {
            'Authorization': `Bearer ${cookies.jwt}`
          }
        });
        if (response.ok) {
          const data = await response.json();
          setTasks(data);

        } else {
          console.error('Failed to fetch tasks:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
      finally{
        setLoading(false);
      }
    };

    fetchTasks();
  }, [cookies.jwt,location]);

  const getCompletionLabel = (completionType) => {
    switch (completionType) {
      case 'live':
        return "Mark as Completed";
      case 'timeup':
        return "Timeup";
      case 'completed':
        return "Marked as Done";
      default:
        return "Unknown Status";
    }
  };

  const getCompletionColor = (completionType) => {
    if (completionType === 'live') return "black";
    if (completionType === 'timeup') return "red";
    if (completionType === 'completed') return "grey";
    return "black";
  };

  const formatDate = (date) => `${new Date(date).getDate()} ${new Intl.DateTimeFormat('en-US', { month: 'short' }).format(new Date(date)).toUpperCase()} - ${new Date(date).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', hour12: true })}`;


  return (
    <TaskGroup>
      {tasks.length > 0 && !loading ? (
        tasks.map((task, index) => (
          <TaskItem key={index} style={{background: task.completionType=="live" ? "rgb(247 247 247)" : task.completionType=="completed" ? "rgb(204 204 204)" : "rgb(247 247 247)"}}> {/* Using index as key */}
            <div style={{ display: "flex", flexDirection: "column", gap: "4px", padding: "0px 16px" }}>
              <h2 style={{ textAlign: "left", margin: "16px 0px" }}>{task.taskName}</h2>
              <div style={{ display: "flex", flexDirection: "row", gap: "24px" }}>
                <p style={{ color: "#0562a5", textDecoration: "underline", cursor: "pointer" }}onClick={() => {
  navigate(`/${task.category}`);
  window.location.reload();
}}>
                  <svg style={{ ...svgIconStyle, marginRight: "6px", height: "20px", width: "20px", fill: "black", verticalAlign: "bottom" }} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
                    <path d="M16,17H5V7H16L19.55,12M17.63,5.84C17.27,5.33 16.67,5 16,5H5A2,2 0 0,0 3,7V17A2,2 0 0,0 5,19H16C16.67,19 17.27,18.66 17.63,18.15L22,12L17.63,5.84Z" />
                  </svg>
                  {task.category}
                </p>
                <p style={{ color: getCompletionColor(task.completionType) }}>
                  <svg style={{ ...svgIconStyle, height: "20px", width: "20px", marginRight: "6px", fill: getCompletionColor(task.completionType), verticalAlign: "bottom" }} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
                    <path d="M12,20A7,7 0 0,1 5,13A7,7 0 0,1 12,6A7,7 0 0,1 19,13A7,7 0 0,1 12,20M12,4A9,9 0 0,0 3,13A9,9 0 0,0 12,22A9,9 0 0,0 21,13A9,9 0 0,0 12,4M12.5,8H11V14L15.75,16.85L16.5,15.62L12.5,13.25V8M7.88,3.39L6.6,1.86L2,5.71L3.29,7.24L7.88,3.39M22,5.72L17.4,1.86L16.11,3.39L20.71,7.25L22,5.72Z" />
                  </svg>
                  {formatDate(task.time)}
                </p>
              </div>
            </div>
            <div style={{ display: "flex", flexDirection: "row", marginTop:"24px", gap: "12px" }}>
              {task.completionType !== 'completed' && (
                <EditButton onClick={() => { navigate(`?update=${task.taskId}`); }}>
                  <svg style={{ ...svgIconStyle, fill: "black", height: "20px", width: "20px" }} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
                    <path d="M14.06,9L15,9.94L5.92,19H5V18.08L14.06,9M17.66,3C17.41,3 17.15,3.1 16.96,3.29L15.13,5.12L18.88,8.87L20.71,7.04C21.1,6.65 21.1,6 20.71,5.63L18.37,3.29C18.17,3.09 17.92,3 17.66,3M14.06,6.19L3,17.25V21H6.75L17.81,9.94L14.06,6.19Z" />
                  </svg>
                </EditButton>
              )}
              <PrimaryButton style={{ height: "20px", background: getCompletionColor(task.completionType), color: "white" }} onClick={() => {
  if (task.completionType === "live") {
    setAsCompleted(task.taskId);
  } else if (task.completionType === "completed") {
    setAsLive(task.taskId);
  }
}}>
                {getCompletionLabel(task.completionType)}
              </PrimaryButton>
            </div>
          </TaskItem>
        ))
      ) : loading ? (<p>Loading...</p>): (
        <p>No tasks available.</p>
      )}
    </TaskGroup>
  );
}

export default Task;
