import Task from "./Task";
import { PrimaryButton, svgIconStyle, BodyContainer, TaskGroup, TaskItem } from "./Utility";

function TaskColumn({title, editPopup, editPopupState, setEditPopupOpenState, setCategory, taskId, setTaskId, page, setPage}) {
    return (
    <TaskGroup style={{flex: '0 0 55%'}}>
          <h2 style={{textAlign:"left", marginLeft:"24px"}}>Tasks for {title=="Home"?"Today":title}</h2>
          {Array.from({ length: 5 }, (_, index) => (
                <Task editPopupState = {editPopupState} setEdit={editPopup} setEditPopupOpenState = {setEditPopupOpenState} setCategory = {setCategory}  taskId={taskId} setTaskId={setTaskId} id={index}/>
            ))}
          <div style={{display:"flex", flexDirection:"row", padding:"32px", justifyContent:"center", gap:"24px"}}>
            <a style={{textDecoration:"underline", color:"grey"}}>Prev</a>
            <a style={{color:"#0562a5"}}>Prev</a>
            <a style={{textDecoration:"underline", color:"#0562a5"}}>Next</a>
          </div>
        </TaskGroup>
  );
}

export default TaskColumn;
