import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { useCookies } from 'react-cookie';

const Suggested = () => {
  const [task, setTask] = useState(null);
  const navigate = useNavigate();
  const [cookies] = useCookies(['jwt']);

  useEffect(() => {
    const fetchSuggestedTask = async () => {
      try {
        const response = await fetch('http://localhost:5000/task/suggested', {
          headers: {
            'Authorization': `Bearer ${cookies.jwt}`,
          }
        });

        if (response.ok) {
          const data = await response.json();
          setTask(data);
        } else {
          toast.error("Failed to fetch suggested task.");
        }
      } catch (error) {
        toast.error("An unexpected error occurred.");
      }
    };

    fetchSuggestedTask();
  }, [cookies.jwt]);

  const handleComplete = async () => {
    if (task) {
      try {
        const response = await fetch(`http://localhost:5000/task/suggested-completed`, {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${cookies.jwt}`,
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ taskId: task.taskId })
        });

        if (response.ok) {
          toast.success("Task marked as completed!");
          setTask(null);
        } else {
          toast.error("Error marking task as completed.");
        }
      } catch (error) {
        toast.error("An unexpected error occurred.");
      }
    }
  };

  const handleBadSuggestion = async () => {
    if (task) {
      try {
        const response = await fetch(`http://localhost:5000/task/suggested-bad`, {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${cookies.jwt}`,
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ taskId: task.taskId })
        });

        if (response.ok) {
          toast.success("Task suggestion marked as bad!");
          setTask(null);
        } else {
          toast.error("Error marking task suggestion as bad.");
        }
      } catch (error) {
        toast.error("An unexpected error occurred.");
      }
    }
  };

  if (!task) return <div>No suggested tasks available.</div>;

  return (
    <TaskContainer key={task.taskId} completionType={task.completionType}>
      <TaskDetails>
        <TaskHeader>{task.taskName}</TaskHeader>
        <CompletionInfo>
          <span style={{ color: '#5189b9', fontWeight: '600' }}>{task.category}</span>
          <span style={{ margin: '0 8px', color: "#7b7b7b" }}>â€¢</span>
          <span style={{ color: '#7b7b7b', fontWeight: '600' }}>{new Date(task.time).toLocaleDateString()}</span>
        </CompletionInfo>
      </TaskDetails>
      <ActionButtons>
        <EditButton onClick={() => navigate(`?update=${task.taskId}`)}>
          <svg style={{ fill: "black", height: "20px", width: "20px" }} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
            <path d="M14.06,9L15,9.94L5.92,19H5V18.08L14.06,9M17.66,3C17.41,3 17.15,3.1 16.96,3.29L15.13,5.12L18.88,8.87L20.71,7.04C21.1,6.65 21.1,6 20.71,5.63L18.37,3.29C18.17,3.09 17.92,3 17.66,3M14.06,6.19L3,17.25V21H6.75L17.81,9.94L14.06,6.19Z" />
          </svg>
        </EditButton>
        <CompleteButton style={{ backgroundColor: 'green' }} onClick={handleComplete}>
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" style={{ height: '20px', width: '20px', fill: 'white', marginRight: '4px' }}>
            <path d="M21,7L9,19L3.5,13.5L4.91,12.09L9,16.17L19.59,5.59L21,7Z" />
          </svg>
        </CompleteButton>
        <CompleteButton style={{ backgroundColor: 'red' }} onClick={handleBadSuggestion}>
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" style={{ height: '20px', width: '20px', fill: 'white', marginRight: '4px' }}>
            <path d="M12,20A7,7 0 0,1 5,13A7,7 0 0,1 12,6A7,7 0 0,1 19,13A7,7 0 0,1 12,20M12,4A9,9 0 0,0 3,13A9,9 0 0,0 12,22A9,9 0 0,0 21,13A9,9 0 0,0 12,4M12.5,8H11V14L15.75,16.85L16.5,15.62L12.5,13.25V8" />
          </svg>
        </CompleteButton>
      </ActionButtons>
    </TaskContainer>
  );
};

// Styled Components
const TaskContainer = ({ children, completionType }) => (
  <div style={{
    border: '1px solid #ddd',
    borderRadius: '8px',
    padding: '16px',
    margin: '16px 0',
    backgroundColor: completionType === 'completed' ? '#e0ffe0' : '#fff'
  }}>
    {children}
  </div>
);

const TaskDetails = ({ children }) => (
  <div style={{ marginBottom: '12px' }}>
    {children}
  </div>
);

const TaskHeader = ({ children }) => (
  <h3 style={{ margin: 0 }}>{children}</h3>
);

const CompletionInfo = ({ children }) => (
  <div style={{ fontSize: '14px', color: '#555' }}>
    {children}
  </div>
);

const ActionButtons = ({ children }) => (
  <div style={{
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center'
  }}>
    {children}
  </div>
);

const EditButton = ({ children, onClick }) => (
  <button style={{
    background: 'transparent',
    border: 'none',
    cursor: 'pointer'
  }} onClick={onClick}>
    {children}
  </button>
);

const CompleteButton = ({ children, onClick, style }) => (
  <button style={{
    padding: '8px 12px',
    border: 'none',
    borderRadius: '4px',
    color: '#fff',
    cursor: 'pointer',
    ...style
  }} onClick={onClick}>
    {children}
  </button>
);

export default Suggested;
