import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useCookies } from 'react-cookie';
import { LoginContainer, Title, Login, PrimaryButton, SecondaryButton } from "./Utility";

function AuthSignup() {
    const [otpSent, setOtpSent] = useState(false);
    const [loading, setLoading] = useState(false);
    const [errorMessage, setErrorMessage] = useState("");
    const navigate = useNavigate();
    const [cookies, setCookie] = useCookies(['jwt']);
    const [sform, setSForm] = useState({
        name: "",
        email: "",
        password: "",
        otp: "" 
    });

    const handleChanges = (e) => {
        const { name, value } = e.target;
        setSForm((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setErrorMessage("");

        try {
            if (!otpSent) {
                const response = await fetch('http://localhost:5000/auth/send-otp', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ email: sform.email }),
                });

                if (response.ok) {
                    setOtpSent(true);
                } else {
                    const data = await response.json();
                    setErrorMessage(data.message || "Error sending OTP");
                }
            } else {
                const response = await fetch('http://localhost:5000/auth/signup', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(sform),
                });

                const data = await response.json();
                if (response.ok) {
                    setCookie('jwt', data.token, { path: '/', maxAge: 3600 });
                    navigate("/");
                } else {
                    setErrorMessage(data.message || "Error during signup");
                }
            }
        } catch (error) {
            setErrorMessage("An unexpected error occurred.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ minWidth: "1080px" }}>
            <LoginContainer>
                <Title>
                    <h1>PLAN-R</h1>
                    <p>An Intelligent activity planner and tracker</p>
                </Title>
                <Login>
                    <h2 style={{ alignSelf: "center" }}>{otpSent ? "Verify OTP" : "Signup"}</h2>
                    {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
                    <form onSubmit={handleSubmit} style={styles.form}>
                        <div style={styles.formGroup}>
                            <label htmlFor="name" style={styles.label}>Name:</label>
                            <input
                                type="text"
                                id="name"
                                name="name"
                                value={sform.name}
                                onChange={handleChanges}
                                readOnly={otpSent}
                                style={styles.input}
                            />
                        </div>
                        <div style={styles.formGroup}>
                            <label htmlFor="email" style={styles.label}>Email:</label>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                value={sform.email}
                                onChange={handleChanges}
                                readOnly={otpSent}
                                style={styles.input}
                            />
                        </div>
                        {otpSent && (
                            <>
                                <div style={styles.formGroup}>
                                    <label htmlFor="otp" style={styles.label}>6-digit One-Time PIN:</label>
                                    <input
                                        type="text"
                                        id="otp"
                                        name="otp"
                                        value={sform.otp}
                                        onChange={handleChanges}
                                        style={styles.input}
                                    />
                                </div>
                                <div style={styles.formGroup}>
                                    <label htmlFor="password" style={styles.label}>Password:</label>
                                    <input
                                        type="password"
                                        id="password"
                                        name="password"
                                        value={sform.password}
                                        onChange={handleChanges}
                                        style={styles.input}
                                    />
                                </div>
                            </>
                        )}
                        <PrimaryButton onClick={handleSubmit} disabled={loading}>
                            {loading ? "Processing..." : (otpSent ? "Complete Signup" : "Send OTP")}
                        </PrimaryButton>
                    </form>
                    <SecondaryButton onClick={() => navigate("/login")}>Already have an account?</SecondaryButton>
                </Login>
            </LoginContainer>
        </div>
    );
}

export default AuthSignup;
