import logo from './logo.svg';
import './App.css';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import ContentBody from './components/ContentBody';
import { useState } from 'react';
import TaskEdit from './components/TaskEdit';

function App() {
  const [editPopupOpen, setEditPopupOpen]  =  useState(false);
  return (
    <div className="App" style={{minWidth:"1080px"}}>
      {editPopupOpen && <TaskEdit editPopupState = {editPopupOpen} setEditPopupOpenState = {setEditPopupOpen}/>}
      <Header />
      <div style={{ display: "flex", flexDirection: "row", height: "100vh",width : "100%" }}>
        <Sidebar editPopupState = {editPopupOpen} setEditPopupOpenState = {setEditPopupOpen}/>
        <ContentBody editPopupState = {editPopupOpen} setEditPopupOpenState = {setEditPopupOpen}/>
      </div>
    </div>
  );
}

export default App;
