import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { PrimaryButton, svgIconStyle, TaskItem, TaskContainer, TaskHeader, TaskFooter } from "./Utility";
import styled from "styled-components";
import { useCookies } from "react-cookie";

function Task() {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState([]);
  const [cookies] = useCookies(['jwt']);

  const EditButton = styled(PrimaryButton)`
    height: 32px;
    background: rgb(235, 235, 235);
    padding: 8px 16px;
    font-weight: bold;

    &:hover {
      background: rgb(249, 206, 137);
    }
  `;

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await fetch(`http://localhost:5000/task/`, {
          headers: {
            'Authorization': `Bearer ${cookies.jwt}`
          }
        });
        if (response.ok) {
          const data = await response.json();
          setTasks(data);
        } else {
          console.error('Failed to fetch tasks:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, [cookies.jwt]);

  const getCompletionLabel = (completionType) => {
    switch (completionType) {
      case 'live':
        return "Mark as Completed";
      case 'timeup':
        return "Timeup";
      case 'completed':
        return "Marked as Done";
      default:
        return "Unknown Status";
    }
  };

  const getCompletionColor = (completionType) => {
    if (completionType === 'live') return "black";
    if (completionType === 'timeup') return "red";
    if (completionType === 'completed') return "grey";
    return "black";
  };

  return (
    <TaskContainer>
      {tasks.length > 0 ? (
        tasks.map((task, index) => (
          <TaskItem key={index}>
            <TaskHeader>
              <h3>{task.taskName}</h3>
              <div>
                <p style={{ color: "#0562a5", textDecoration: "underline", cursor: "pointer" }}>
                  <svg style={{ ...svgIconStyle, marginRight: "6px" }} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
                    <path d="M16,17H5V7H16L19.55,12M17.63,5.84C17.27,5.33 16.67,5 16,5H5A2,2 0 0,0 3,7V17A2,2 0 0,0 5,19H16C16.67,19 17.27,18.66 17.63,18.15L22,12L17.63,5.84Z" />
                  </svg>
                  {task.category}
                </p>
                <p style={{ color: getCompletionColor(task.completionType) }}>
                  <svg style={{ ...svgIconStyle, marginRight: "6px", fill: getCompletionColor(task.completionType) }} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
                    <path d="M12,20A7,7 0 0,1 5,13A7,7 0 0,1 12,6A7,7 0 0,1 19,13A7,7 0 0,1 12,20M12,4A9,9 0 0,0 3,13A9,9 0 0,0 12,22A9,9 0 0,0 21,13A9,9 0 0,0 12,4M12.5,8H11V14L15.75,16.85L16.5,15.62L12.5,13.25V8M7.88,3.39L6.6,1.86L2,5.71L3.29,7.24L7.88,3.39M22,5.72L17.4,1.86L16.11,3.39L20.71,7.25L22,5.72Z" />
                  </svg>
                  {new Date(task.time).toLocaleString()}
                </p>
              </div>
            </TaskHeader>
            <TaskFooter>
              {task.completionType !== 'completed' && (
                <EditButton onClick={() => navigate(`?update=${task.taskId}`)}>
                  Edit
                </EditButton>
              )}
              <PrimaryButton style={{ background: getCompletionColor(task.completionType), color: "black" }}>
                {getCompletionLabel(task.completionType)}
              </PrimaryButton>
            </TaskFooter>
          </TaskItem>
        ))
      ) : (
        <p>No tasks available.</p>
      )}
    </TaskContainer>
  );
}

export default Task;
