import ForgotPassword from "./ForgotPassword";
import { LoginContainer, Title, AuthContainer, Login, PrimaryButton, SecondaryButton } from "./Utility";
import { useState } from "react";

function AuthLogin({ setAuth }) {
  const [lform, setLForm] = useState({
    email: "",
    password: ""
  });

  const handleChanges = (e) => {
    const { name, value } = e.target;
    setLForm((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data:', lform);
    setAuth(true);
  };

  return (
    <div style={{ minWidth: "1080px" }}>
      <LoginContainer>
        <Title>
          <h1>PLAN-R</h1>
          <p>An Intelligent activity planner and tracker</p>
        </Title>
        <AuthContainer>
          <Login>
            <h2>Login</h2>
            <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '24px', padding: '20px', maxWidth: '400px', width: '100%' }}>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                <label htmlFor="email" style={{ fontSize: '16px', fontWeight: 'bold', textAlign: "left" }}>Email:</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={lform.email}
                  onChange={handleChanges}
                  style={{ padding: '10px', border: '1px solid #ddd', borderRadius: '4px', fontSize: '16px' }}
                />
              </div>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                <label htmlFor="password" style={{ fontSize: '16px', fontWeight: 'bold', textAlign: "left" }}>Password:</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={lform.password}
                  onChange={handleChanges}
                  style={{ padding: '10px', border: '1px solid #ddd', borderRadius: '4px', fontSize: '16px' }}
                />
              </div>
              <PrimaryButton type="submit">Login</PrimaryButton>
              <SecondaryButton onClick={() => {/* Handle forgot password */}}>Forgot Password</SecondaryButton>
            </form>
          </Login>
        </AuthContainer>
      </LoginContainer>
    </div>
  );
}

export default AuthLogin;
